.calendar {
  width: 400px;
  box-sizing: border-box;
  padding: 18px;
  color: #000;
}

.calendar span {
  font-size: 16px;
  line-height: 16px;
  user-select: none;
}

.calendar .banner {
  font-size: 0;
  line-height: 16px;
  text-align: center;
}
.arrow {
  display: inline-block;
  width: 0;
  height: 0;
  border-top: 8px solid transparent;
  border-bottom: 8px solid transparent;
  cursor: pointer;
  user-select: none;
}
.arrow-left {
  border-left: 8px solid transparent;
  border-right: 8px solid #409eff;
  margin-right: 16px;
}

.arrow-right {
  border-right: 8px solid transparent;
  border-left: 8px solid #409eff;
  margin-left: 16px;
}

.calendar .date-item {
  box-sizing: border-box;
  position: relative;
  display: inline-block;
  width: 48px;
  line-height: 48px;
  padding: 4px;
  text-align: center;
  color: #c0c4cc;
  user-select: none;
}

.calendar .date-item span {
  padding: 8px;
}

.calendar .date-item__week {
  color: #000 !important;
}

.calendar .current-month {
  color: #000 !important;
}

.calendar .current-month:hover {
  background-color: #f2f8fe;
  border-radius: 50%;
  cursor: pointer;
}

.calendar .today {
  color: #409eff !important;
}

.calendar .is-selected {
  background-color: #409eff !important;
  color: #fff !important;
  border-radius: 50%;
}

.calendar .is-marked::after {
  content: '';
  position: absolute;
  border-radius: 50%;
  border: 1.5px solid #000;
  left: 50%;
  transform: translateX(-50%);
  bottom: 5px;
}

.calendar .comment {
  padding: 16px;
  color: #c0c4cc;
}
